<?php		// Classes	require_once("../includes/classes/class.webSocket.php" );	require_once("../includes/classes/class.xmlManipulation.php");		class FlashPolicyServerDaemon extends Daemon	{		public function start()		{			$socket = stream_socket_server("tcp://0.0.0.0:843", $errno, $errstr);			if (!$socket)			{				echo "$errstr ($errno)<br />\n";			}			else			{				while ($conn = stream_socket_accept($socket, -1))				{					fwrite($conn, '<?xml version="1.0"?><!DOCTYPE cross-domain-policy SYSTEM "http://www.macromedia.com/xml/dtds/cross-domain-policy.dtd"><cross-domain-policy><allow-access-from domain="*" to-ports="*"/></cross-domain-policy>');					fclose($conn);				}				fclose($socket);			}		}			}	?>